import React from "react";

const DisplayParts = ({parts, category}) => {
    
    const computerPartsArray = [];

    

    return (
        <div>
            {category.map(category => (
                        <form onSubmit={handleFormSubmit}>
                            <h2>{capitalize(category)}</h2>
                            <div className="display-flex">
                            {parts.map(parts => {
                                if(parts.category === category)
                                
                                return  <div className="container-1">
                                        <h3>{parts.name}</h3>
                                        <p>{parts.Description} </p>
                                        <p>${parts.price}</p>
                                        <button type="button" onClick={function(event){ selectPart(event, parts.id);}}>Add</button>
                                    </div>              
                            }
                            )}
                             </div>
                        </form>
            ))}
        <button className="btn col-12 col-md-3" type="submit">
            Create
        </button>
        </div>
    )
}

export default DisplayParts
