import React from "react";
import DisplayParts from "../components/DisplayParts";
import { useQuery } from "@apollo/client";
import { QUERY_ALL_PRODUCTS, QUERY_CATEGORIES } from "../utils/queries";

const CreateComputer = () => {
    const part = useQuery(QUERY_ALL_PRODUCTS)
    const categories = useQuery(QUERY_CATEGORIES)

    const errors = part.error || categories.error
    

    const theCategory = ['Case','Motherboard','CPU','GPU','RAM','storage_device','Cooling','power_supply_unit','monitor','OS','Keyboard','mouse']

    return (
        <main className="container-2">
            <DisplayParts
            // parts={parts}
            category={theCategory}></DisplayParts>
        </main>
    )
}

export default CreateComputer
