import React from "react";

const handleFormSubmit = event => {
    event.preventDefault();
    console.log('Working')
}

const selectPart = (event) => {
const e = event
   if (e.target.textContent === 'Add') {
        e.target.textContent = 'Remove'
        e.target.style.backgroundColor = "red";
   } else {
    e.target.textContent = 'Add'
    e.target.style.backgroundColor = "green";
   }
}

function capitalize(str) {
    var i, frags = str.split('_');
    for (i=0; i<frags.length; i++) {
      frags[i] = frags[i].charAt(0).toUpperCase() + frags[i].slice(1);
    }
    return frags.join(' ');
  }

const DisplayParts = ({parts, category}) => {
    console.log(parts)

    return (
        <div>
            {category.map(category => (
                        <form onSubmit={handleFormSubmit}>
                            <h2>{capitalize(category)}</h2>
                            <div className="display-flex">
                            {parts.map(parts => {
                                if(parts.category === category)
                                
                                return  <div className="container-1">
                                        <h3>{parts.name}</h3>
                                        <p>{parts.Description} </p>
                                        <p>${parts.price}</p>
                                        <button type="button" onClick={() => selectPart(this.event)}>Add</button>
                                    </div>              
                            }
                            )}
                             </div>
                        </form>
            ))}
        <button className="btn col-12 col-md-3" type="submit">
            Create
        </button>
        </div>
    )
}

export default DisplayParts
